import React from "react";

import axios from 'axios';

const path = "http://localhost:8000"


export async function axios_post_data_save(data)
{
    await axios.post(path+"/data/save", 
        {data: JSON.stringify(data)});
}

export async function axios_get_data_load(data)
{
    const get_data =  await axios.get(path+"/data/load", { params: {date: data}});
    return get_data;
}

export async function axios_get_data_list()
{
    const get_data = await axios.get(path+"/data/list", );
    const json_data = JSON.parse(get_data.data);
    console.log(json_data);
    return json_data;
}
